$(window).load(function () {
    $('.flexslider').flexslider({
        animation: "slide",
        slideshowSpeed: 5000,
        pauseOnAction: true,
        pauseOnHover: true,
        directionNav: true,
        controlNav: true,
        prevText: "Previous",
        nextText: "Next",
        slideshow: true,
        animation: 'fade',
        animationLoop: true
    });
});